// Generated by CoffeeScript 1.7.1
$(document).on('page:change', function() {
  var edit_form;
  if ($('#my-awesome-dropzone').length > 0) {
    edit_form = new Dropzone("#my-awesome-dropzone", {
      paramName: "photo[photo]",
      addRemoveLinks: true,
      autoProcessQueue: false,
      parallelUploads: 10
    });
    edit_form.on("success", function(file) {
      console.log("successfully uploaded file ");
      $('body').append(file);
      return location.reload();
    });
    edit_form.on("error", function(file) {
      console.log("error occurred while uploading file");
      return $('#status_message').html("<b style='color: red;'>An error occurred while saving the Student.</b>");
    });
    $("#edit_form_save_button").click(function() {
      console.log("Save Button Clicked");
      if (edit_form.files.length > 0) {
        console.log("found file to process");
        return edit_form.processQueue();
      }
    });
  }
  return $('a[href="' + this.location.pathname + '"]').parent().addClass('active');
});
